  <CartContext.Consumer>
    {(value) => {
      const { cartList } = value;
      const showEmptyView = cartList.length === 0;
      // TODO: Update the functionality to remove all the items in the cart
      return (
        <>
          <Header />
          <div className="cart-container">
            {showEmptyView ? (
              <EmptyCartView />
